import{_ as a,c as o,o as t,ag as i}from"./chunks/framework.Cs5y-aXb.js";const T=JSON.parse('{"title":"Contracts","description":"","frontmatter":{},"headers":[],"relativePath":"dev-log/contracts.md","filePath":"dev-log/contracts.md","lastUpdated":1757667594000}'),d={name:"dev-log/contracts.md"};function l(c,e,n,r,h,u){return t(),o("div",null,e[0]||(e[0]=[i('<h1 id="contracts" tabindex="-1">Contracts <a class="header-anchor" href="#contracts" aria-label="Permalink to &quot;Contracts&quot;">​</a></h1><h2 id="contract-1-单条任务生成-qr-码-通用约束" tabindex="-1">Contract #1: 单条任务生成 QR 码（通用约束） <a class="header-anchor" href="#contract-1-单条任务生成-qr-码-通用约束" aria-label="Permalink to &quot;Contract #1: 单条任务生成 QR 码（通用约束）&quot;">​</a></h2><h3 id="given" tabindex="-1">Given <a class="header-anchor" href="#given" aria-label="Permalink to &quot;Given&quot;">​</a></h3><ul><li>版本 0.4.1；日期：2025-09-12</li><li>数据来源：Todo 或 Schedule</li><li>必备字段：<code>id</code>，<code>dueRange[0]</code>（若为 Schedule）或 <code>startTime</code>（若为 Todo）</li><li>可选字段：<code>location</code> / <code>priority</code> / <code>activityTitle</code> / <code>startTime</code> / <code>doneTime</code> / <code>estPomo[]</code> / <code>dueRange[1]</code></li><li>时区：Asia/Shanghai（+08:00）</li></ul><h3 id="when" tabindex="-1">When <a class="header-anchor" href="#when" aria-label="Permalink to &quot;When&quot;">​</a></h3><ul><li>选择该条任务</li><li>点击 <code>QrCode24Regular</code> 按钮生成二维码</li><li>使用手机相机或系统扫码工具扫描，解析得到 ICS 文本</li></ul><h3 id="then" tabindex="-1">Then <a class="header-anchor" href="#then" aria-label="Permalink to &quot;Then&quot;">​</a></h3><ul><li>生成的 ICS 满足本场景的时间规则与字段要求</li><li>字段名大写，时间含时区或 UTC；行长遵循软换行</li><li>UID 稳定且唯一（规则建议：<code>&lt;type&gt;-&lt;id&gt;@your.domain</code> 或 UUIDv5），不依赖时间戳</li><li><code>SUMMARY</code> 映射 <code>activityTitle</code>（若缺失，回退到 <code>Schedule.activityTitle</code> 或 <code>Todo.activityTitle</code> 任一存在者）</li><li><code>DESCRIPTION</code> 可包含 <code>location</code>、<code>priority</code>（格式参见“字段映射与校验清单”）</li></ul><hr><h2 id="contract-2-单条任务生成-qr-码-todo-无开始-结束时间-→-全天" tabindex="-1">Contract #2: 单条任务生成 QR 码（Todo 无开始/结束时间 → 全天） <a class="header-anchor" href="#contract-2-单条任务生成-qr-码-todo-无开始-结束时间-→-全天" aria-label="Permalink to &quot;Contract #2: 单条任务生成 QR 码（Todo 无开始/结束时间 → 全天）&quot;">​</a></h2><h3 id="given-1" tabindex="-1">Given <a class="header-anchor" href="#given-1" aria-label="Permalink to &quot;Given&quot;">​</a></h3><ul><li>数据：Todo</li><li>必备字段：<code>Todo.id</code>，<code>Todo.activityTitle</code></li><li>时间：无 <code>startTime</code>、无 <code>doneTime</code></li></ul><h3 id="when-1" tabindex="-1">When <a class="header-anchor" href="#when-1" aria-label="Permalink to &quot;When&quot;">​</a></h3><ul><li>如 Contract #1</li></ul><h3 id="then-1" tabindex="-1">Then <a class="header-anchor" href="#then-1" aria-label="Permalink to &quot;Then&quot;">​</a></h3><ul><li>导出为全天事件：<code>DTSTART;VALUE=DATE=&lt;YYYYMMDD&gt;</code>，<code>DTEND;VALUE=DATE=&lt;YYYYMMDD+1&gt;</code></li><li><code>SUMMARY=&lt;Todo.activityTitle&gt;</code></li><li><code>DESCRIPTION</code> 可包含 <code>priority</code></li></ul><hr><h2 id="contract-3-单条任务生成-qr-码-todo-有-starttime-estpomo" tabindex="-1">Contract #3: 单条任务生成 QR 码（Todo 有 startTime + estPomo） <a class="header-anchor" href="#contract-3-单条任务生成-qr-码-todo-有-starttime-estpomo" aria-label="Permalink to &quot;Contract #3: 单条任务生成 QR 码（Todo 有 startTime + estPomo）&quot;">​</a></h2><h3 id="given-2" tabindex="-1">Given <a class="header-anchor" href="#given-2" aria-label="Permalink to &quot;Given&quot;">​</a></h3><ul><li>数据：Todo</li><li>必备字段：<code>Todo.id</code>，<code>Todo.activityTitle</code>，<code>Todo.startTime（含时区）</code>，<code>Todo.estPomo[]</code></li><li>说明：时长 (N = \\max(1, \\sum estPomo))，总时长 = (N \\times 30) 分钟</li></ul><h3 id="when-2" tabindex="-1">When <a class="header-anchor" href="#when-2" aria-label="Permalink to &quot;When&quot;">​</a></h3><ul><li>如 Contract #1</li></ul><h3 id="then-2" tabindex="-1">Then <a class="header-anchor" href="#then-2" aria-label="Permalink to &quot;Then&quot;">​</a></h3><ul><li><code>DTSTART = startTime</code></li><li><code>DTEND = startTime + N×30min</code></li><li>其他字段映射同 Contract #2</li></ul><hr><h2 id="contract-4-单条任务生成-qr-码-todo-有-starttime-donetime" tabindex="-1">Contract #4: 单条任务生成 QR 码（Todo 有 startTime + doneTime） <a class="header-anchor" href="#contract-4-单条任务生成-qr-码-todo-有-starttime-donetime" aria-label="Permalink to &quot;Contract #4: 单条任务生成 QR 码（Todo 有 startTime + doneTime）&quot;">​</a></h2><h3 id="given-3" tabindex="-1">Given <a class="header-anchor" href="#given-3" aria-label="Permalink to &quot;Given&quot;">​</a></h3><ul><li>数据：Todo</li><li>必备字段：<code>Todo.id</code>，<code>Todo.activityTitle</code>，<code>Todo.startTime</code>，<code>Todo.doneTime</code></li><li>说明：不使用 <code>estPomo</code> 计算</li></ul><h3 id="when-3" tabindex="-1">When <a class="header-anchor" href="#when-3" aria-label="Permalink to &quot;When&quot;">​</a></h3><ul><li>如 Contract #1</li></ul><h3 id="then-3" tabindex="-1">Then <a class="header-anchor" href="#then-3" aria-label="Permalink to &quot;Then&quot;">​</a></h3><ul><li><code>DTSTART = startTime</code></li><li><code>DTEND = doneTime</code></li><li>其他字段映射同 Contract #2</li></ul><hr><h2 id="contract-5-单条任务生成-qr-码-schedule-有-duerange-0-duerange-1" tabindex="-1">Contract #5: 单条任务生成 QR 码（Schedule 有 dueRange[0], dueRange[1]） <a class="header-anchor" href="#contract-5-单条任务生成-qr-码-schedule-有-duerange-0-duerange-1" aria-label="Permalink to &quot;Contract #5: 单条任务生成 QR 码（Schedule 有 dueRange[0], dueRange[1]）&quot;">​</a></h2><h3 id="given-4" tabindex="-1">Given <a class="header-anchor" href="#given-4" aria-label="Permalink to &quot;Given&quot;">​</a></h3><ul><li>数据：Schedule</li><li>必备字段：<code>Schedule.id</code>，<code>Schedule.activityTitle</code>，<code>Schedule.dueRange[0]</code>（开始），<code>Schedule.dueRange[1]</code>（结束）</li><li>可选字段：<code>Schedule.location</code></li></ul><h3 id="when-4" tabindex="-1">When <a class="header-anchor" href="#when-4" aria-label="Permalink to &quot;When&quot;">​</a></h3><ul><li>如 Contract #1</li></ul><h3 id="then-4" tabindex="-1">Then <a class="header-anchor" href="#then-4" aria-label="Permalink to &quot;Then&quot;">​</a></h3><ul><li><code>DTSTART = dueRange[0]</code></li><li><code>DTEND = dueRange[1]</code></li><li><code>SUMMARY = Schedule.activityTitle</code></li><li><code>DESCRIPTION</code> 可包含 <code>location</code></li></ul><hr><h2 id="contract-6-单条任务生成-qr-码-schedule-仅有-duerange-0-无时长-→-系统默认" tabindex="-1">Contract #6: 单条任务生成 QR 码（Schedule 仅有 dueRange[0] 无时长 → 系统默认） <a class="header-anchor" href="#contract-6-单条任务生成-qr-码-schedule-仅有-duerange-0-无时长-→-系统默认" aria-label="Permalink to &quot;Contract #6: 单条任务生成 QR 码（Schedule 仅有 dueRange[0] 无时长 → 系统默认）&quot;">​</a></h2><h3 id="given-5" tabindex="-1">Given <a class="header-anchor" href="#given-5" aria-label="Permalink to &quot;Given&quot;">​</a></h3><ul><li>数据：Schedule</li><li>必备字段：<code>Schedule.id</code>，<code>Schedule.activityTitle</code>，<code>Schedule.dueRange[0]</code></li><li>无 <code>dueRange[1]</code></li></ul><h3 id="when-5" tabindex="-1">When <a class="header-anchor" href="#when-5" aria-label="Permalink to &quot;When&quot;">​</a></h3><ul><li>如 Contract #1</li></ul><h3 id="then-5" tabindex="-1">Then <a class="header-anchor" href="#then-5" aria-label="Permalink to &quot;Then&quot;">​</a></h3><ul><li><code>DTSTART = dueRange[0]</code></li><li><code>DTEND = dueRange[0] + 默认时长</code></li><li>兼容性说明：不同系统可能显示为与开始时间相同或 +1 小时</li><li>其他字段映射同 Contract #5</li></ul>',48)]))}const m=a(d,[["render",l]]);export{T as __pageData,m as default};
