import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.Cs5y-aXb.js";const p=JSON.parse('{"title":"活动清单","description":"","frontmatter":{},"headers":[],"relativePath":"guide/activity.md","filePath":"guide/activity.md","lastUpdated":1750935487000}'),r={name:"guide/activity.md"};function o(n,l,u,d,h,s){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="活动清单" tabindex="-1">活动清单 <a class="header-anchor" href="#活动清单" aria-label="Permalink to &quot;活动清单&quot;">​</a></h1><h2 id="术语" tabindex="-1">术语 <a class="header-anchor" href="#术语" aria-label="Permalink to &quot;术语&quot;">​</a></h2><ul><li>活动 Activity：包括如下 3 种类型： <ul><li>预约 Schedule：与他人约定地点日期持续时间的活动，字段包括：描述、地点、持续时间、开始日期时间</li><li>无所事事 Untaetigkeit：与自己约定的发呆，字段包括：描述、地点、持续时间、开始日期时间</li><li>待办 Todo：由原子化时间构成的活动，缺少明确的截止日期，字段包括：描述、类型、预估数量、到期日期 <ul><li>Tomato🍅： 可分配到日程表的工作时间，每个 25min，可进行第一次预估。</li><li>Cherry🍒： 可分配到日程表的工作时间，预设 4 个 15min。</li><li>Grape🍇： 可分配到日程表的生活时间，每个 25min，可进行第一次预估。</li></ul></li><li>打扰：通过记录打扰生成，可以是预约或任务 <ul><li>内部打扰：标记对话 bubble</li><li>外部打扰：标记他人电话</li></ul></li></ul></li></ul><h2 id="功能" tabindex="-1">功能 <a class="header-anchor" href="#功能" aria-label="Permalink to &quot;功能&quot;">​</a></h2><ul><li>选择活动：选中任务</li><li>添加预约</li><li>添加无所事事</li><li>添加任务</li><li>更改任务类型：选中任务</li><li>复制活动：选中活动</li><li>筛选活动： <ul><li>今日到期：任务的 deadline 或者预约的开始时间</li><li>内外打扰</li><li>待办任务：Task</li></ul></li></ul><h2 id="任务状态" tabindex="-1">任务状态 <a class="header-anchor" href="#任务状态" aria-label="Permalink to &quot;任务状态&quot;">​</a></h2><h3 id="执行状态" tabindex="-1">执行状态 <a class="header-anchor" href="#执行状态" aria-label="Permalink to &quot;执行状态&quot;">​</a></h3><ul><li>新添加 ：灰色</li><li>正在进行 ongoing：红色</li><li>退回 suspended：橙色</li><li>延期 delayed：蓝色</li><li>取消 cancelled：黑色</li></ul><h3 id="截止日期" tabindex="-1">截止日期 <a class="header-anchor" href="#截止日期" aria-label="Permalink to &quot;截止日期&quot;">​</a></h3><ul><li>红色：今天到期</li><li>橙色：明天到期</li><li>黄色：后天到期</li><li>蓝色：过期</li></ul><h3 id="选中状态" tabindex="-1">选中状态 <a class="header-anchor" href="#选中状态" aria-label="Permalink to &quot;选中状态&quot;">​</a></h3><ul><li>选中字段变为红色边框</li><li>从今日待办选中的行与活动清单匹配，显示为黄色背景</li></ul><h3 id="任务类型" tabindex="-1">任务类型 <a class="header-anchor" href="#任务类型" aria-label="Permalink to &quot;任务类型&quot;">​</a></h3><ul><li>红色：</li></ul><h3 id="_2-管理活动清单" tabindex="-1">2. <strong>管理活动清单</strong> <a class="header-anchor" href="#_2-管理活动清单" aria-label="Permalink to &quot;2. **管理活动清单**&quot;">​</a></h3><p>管理活动清单并选择待办任务</p><p><strong>操作步骤：</strong></p><ol><li><p>点击增加活动（任务/预约/无所事事）</p></li><li><p>编辑活动内容：</p><ul><li>待办任务（Task）：描述、预估、到期时间 <ul><li>选中任务行，点击切换任务类型</li><li>🍅 匹配工作时间=25min</li><li>🍇 匹配生活时间=25min</li><li>🍒 匹配工作时间=4x15min（沉浸工作法）</li></ul></li><li>预约任务（Schedule）：描述、地点、持续时间、开始时间 <ul><li>无所事事（Untaetigkeit）：描述、地点、持续时间、开始时间</li></ul></li></ul></li><li><p>点击选中一行后，点击删除活动</p></li><li><p>点击筛选获得：今日到期、内外打扰、显示全部、待办任务、预约任务</p></li><li><p>选中任务到今日待办</p><ul><li>预约类任务通过设置开始时间自动添加</li><li>待办任务点击添加，传动到今日待办区域</li></ul></li></ol><h2 id="预期结果" tabindex="-1"><strong>预期结果：</strong> <a class="header-anchor" href="#预期结果" aria-label="Permalink to &quot;**预期结果：**&quot;">​</a></h2><ol><li>添加、删除和编辑活动</li><li>活动用不同的 icon 颜色区分状态</li></ol>',20)]))}const m=i(r,[["render",o]]);export{p as __pageData,m as default};
