import{_ as a}from"./chunks/Tag16Regular.C0ZtvxtF.js";import{_ as e,c as t,o,ag as r}from"./chunks/framework.Cs5y-aXb.js";const i="data:image/svg+xml,%3csvg%20xmlns='http://www.w3.org/2000/svg'%20xmlns:xlink='http://www.w3.org/1999/xlink'%20viewBox='0%200%2016%2016'%3e%3cg%20fill='none'%3e%3cpath%20d='M2.587%207.314a1.988%201.988%200%200%200%200%202.822l3.296%203.28c.783.779%202.053.779%202.836%200l4.67-4.647a1.99%201.99%200%200%200%20.587-1.397L14%204.01A2%202%200%200%200%2011.987%202l-3.25.014a2.01%202.01%200%200%200-1.41.584l-4.74%204.716zM11%206a1%201%200%201%201%200-2a1%201%200%200%201%200%202z'%20fill='currentColor'%3e%3c/path%3e%3c/g%3e%3c/svg%3e",p=JSON.parse('{"title":"标签系统使用方法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/tag.md","filePath":"guide/tag.md","lastUpdated":1754644994000}'),n={name:"guide/tag.md"};function d(c,l,s,h,u,_){return o(),t("div",null,l[0]||(l[0]=[r('<h1 id="标签系统使用方法" tabindex="-1">标签系统使用方法 <a class="header-anchor" href="#标签系统使用方法" aria-label="Permalink to &quot;标签系统使用方法&quot;">​</a></h1><h2 id="_1-标签快速使用" tabindex="-1">1 标签快速使用 <a class="header-anchor" href="#_1-标签快速使用" aria-label="Permalink to &quot;1 标签快速使用&quot;">​</a></h2><h3 id="_1-1-标题快速添加标签" tabindex="-1">1.1 标题快速添加标签 <a class="header-anchor" href="#_1-1-标题快速添加标签" aria-label="Permalink to &quot;1.1 标题快速添加标签&quot;">​</a></h3><ul><li><strong>输入#自动推荐：</strong> 在活动标题文本的末尾输入 <code>#标签名</code>，触发标签联想弹窗，显示已有标签列表，不存在标签支持新建； <ul><li>可继续输入字符筛选，方向键上下切换，高亮选中后按回车（Enter），即可关联该标签；</li><li>若输入内容未命中已有标签，回车将自动「新建」该标签并关联。</li></ul></li></ul><h3 id="_1-2-标签按钮操作区" tabindex="-1">1.2 标签按钮操作区 <a class="header-anchor" href="#_1-2-标签按钮操作区" aria-label="Permalink to &quot;1.2 标签按钮操作区&quot;">​</a></h3><p>每行活动右侧均有<strong>标签按钮</strong>，状态如下：</p><ul><li><img src="'+a+'" width="16" style="display:inline-block;vertical-align:middle;margin:0;">　<strong>无标签时</strong>：点击弹出【标签选择器】，可快速添加标签。</li><li><img src="'+i+'" width="16" style="display:inline-block;vertical-align:middle;margin:0;">　<strong>已有标签时</strong>：点击弹出【标签管理器】面板，可批量增/删标签。</li></ul><h3 id="_1-3-交互技巧与快捷键" tabindex="-1">1.3 交互技巧与快捷键 <a class="header-anchor" href="#_1-3-交互技巧与快捷键" aria-label="Permalink to &quot;1.3 交互技巧与快捷键&quot;">​</a></h3><ul><li><strong><code>ALT</code>/ <code>opt</code> + 点击</strong> <img src="'+i+'" width="16" style="display:inline-block;vertical-align:middle;margin:0;">：可切换全部标签显示/隐藏，仅影响视觉显示，不影响数据与筛选。</li><li>在标签选择弹窗中，支持： <ul><li>方向键上下切换标签选项</li><li><code>Enter</code>选中，<code>ESC</code> 退出</li></ul></li><li>支持每个活动打多个标签，单活动建议不超过 10 个标签以确保界面清晰。</li></ul><h2 id="_2-标签管理器面板" tabindex="-1">2 标签管理器面板 <a class="header-anchor" href="#_2-标签管理器面板" aria-label="Permalink to &quot;2 标签管理器面板&quot;">​</a></h2><ul><li>在<code>标签管理器</code>界面可： <ul><li>勾选/取消活动关联的所有标签（支持多选）；</li><li>新建标签（输入新名，自动上色）；</li><li>双击名字，修改已有标签名字；</li><li>移除当前活动的某个标签（直接点击标签旁的删除按钮）；</li><li>标签选择完成，关闭窗口自动保存并计入全局标签统计；</li><li>点击修改文字颜色；</li><li>点击修改背景颜色。</li><li></li></ul></li></ul><h2 id="_3-重要说明" tabindex="-1">3 重要说明 <a class="header-anchor" href="#_3-重要说明" aria-label="Permalink to &quot;3 重要说明&quot;">​</a></h2><ul><li>标签支持自定义颜色和自动统计全局使用次数；</li><li>如需批量清除标签，在管理器界面支持一键移除；</li><li>通过 # 无法生成带有 <code>-</code>标签，可到<code>标签管理器</code>修改</li><li>标签用于筛选/聚合，并与主筛选框组合使用【开发中】。</li></ul>',13)]))}const f=e(n,[["render",d]]);export{p as __pageData,f as default};
