import{_ as e,c as t,o as l,ag as i}from"./chunks/framework.Cs5y-aXb.js";const r="/pomotention/pomodoro-mode.png",a="/pomotention/pizza-mode.png",n="data:image/svg+xml,%3csvg%20xmlns='http://www.w3.org/2000/svg'%20xmlns:xlink='http://www.w3.org/1999/xlink'%20viewBox='0%200%2024%2024'%3e%3cg%20fill='none'%3e%3cpath%20d='M16.242%202.932l4.826%204.826a2.75%202.75%200%200%201-.715%204.404l-4.87%202.435a.75.75%200%200%200-.374.426l-1.44%204.166a1.25%201.25%200%200%201-2.065.476L8.5%2016.561L4.06%2021H3v-1.062L7.44%2015.5l-3.105-3.104a1.25%201.25%200%200%201%20.476-2.066l4.166-1.439a.75.75%200%200%200%20.426-.374l2.435-4.87a2.75%202.75%200%200%201%204.405-.715zm3.766%205.886l-4.826-4.825a1.25%201.25%200%200%200-2.002.324l-2.435%204.871a2.25%202.25%200%200%201-1.278%201.12l-3.789%201.31l6.705%206.704l1.308-3.788a2.25%202.25%200%200%201%201.12-1.278l4.872-2.436a1.25%201.25%200%200%200%20.325-2.002z'%20fill='currentColor'%3e%3c/path%3e%3c/g%3e%3c/svg%3e",d="data:image/svg+xml,%3csvg%20xmlns='http://www.w3.org/2000/svg'%20xmlns:xlink='http://www.w3.org/1999/xlink'%20viewBox='0%200%2024%2024'%3e%3cg%20fill='none'%3e%3cpath%20d='M7.596%2015.35a.75.75%200%200%201%201.06%201.06l-3.095%203.094H7.75a.75.75%200%200%201%20.743.648l.007.102a.75.75%200%200%201-.75.75h-4a.75.75%200%200%201-.75-.75v-4a.75.75%200%200%201%201.5%200v2.19l3.096-3.094zm8.659%205.654a.75.75%200%200%201%200-1.5h2.186L15.35%2016.41a.75.75%200%200%201-.073-.976l.073-.084a.75.75%200%200%201%201.06%200l3.095%203.096v-2.192a.75.75%200%200%201%20.648-.743l.102-.007a.75.75%200%200%201%20.75.75v4a.75.75%200%200%201-.75.75h-4zM7.75%203a.75.75%200%200%201%200%201.5H5.563l3.093%203.095a.75.75%200%200%201%20.072.976l-.073.085a.75.75%200%200%201-1.06%200L4.5%205.557V7.75a.75.75%200%200%201-.648.743L3.75%208.5A.75.75%200%200%201%203%207.75v-4A.75.75%200%200%201%203.75%203h4zm12.505%200a.75.75%200%200%201%20.75.75v4a.75.75%200%200%201-1.5%200V5.559L16.41%208.655a.75.75%200%200%201-.976.073l-.084-.073a.75.75%200%200%201%200-1.06L18.442%204.5h-2.187a.75.75%200%200%201-.743-.648l-.007-.102a.75.75%200%200%201%20.75-.75h4z'%20fill='currentColor'%3e%3c/path%3e%3c/g%3e%3c/svg%3e",w=JSON.parse('{"title":"番茄时钟使用说明","description":"","frontmatter":{},"headers":[],"relativePath":"guide/timer.md","filePath":"guide/timer.md","lastUpdated":1757488227000}'),s={name:"guide/timer.md"};function c(g,o,m,h,p,u){return l(),t("div",null,o[0]||(o[0]=[i('<h1 id="番茄时钟使用说明" tabindex="-1">番茄时钟使用说明 <a class="header-anchor" href="#番茄时钟使用说明" aria-label="Permalink to &quot;番茄时钟使用说明&quot;">​</a></h1><p>番茄时钟分为种模式：单次计时模式、连续计时模式和迷你计时器模式。</p><h2 id="_1-单次计时模式-pomodoro-mode" tabindex="-1">1 单次计时模式 (Pomodoro Mode) <a class="header-anchor" href="#_1-单次计时模式-pomodoro-mode" aria-label="Permalink to &quot;1 单次计时模式 (Pomodoro Mode)&quot;">​</a></h2><p>此模式适用于独立的、单次的工作或休息。 <img src="'+r+'" alt="Pomodoro Mode" width="200"></p><ul><li><strong>开始工作</strong>：点击 <code>Wind up</code> 启动一个标准的工作时钟周期。</li><li><strong>取消计时</strong>：点击 <code>Squash</code> 可提前终止并取消当前的工作计时。</li><li><strong>开始休息</strong>：点击 <code>Break</code> 启动休息计时器。</li><li><strong>设定休息时长</strong>：点击数字按钮展开可设置时间，并选择 (<code>02</code>, <code>05</code>, <code>10</code>, <code>15</code>, <code>30</code>, <code>60</code>) 。</li><li><strong>终止休息</strong>：在休息期间，点击 <code>Stop</code> 可随时结束休息。</li><li><strong>提示音</strong>：工作和休息，分别提供提示音，当时长设置更改，提示时机按比例缩放。</li></ul><h3 id="工作时段声音提示-以-25-分钟计" tabindex="-1"><strong>工作时段声音提示 (以 <code>25</code> 分钟计)</strong> <a class="header-anchor" href="#工作时段声音提示-以-25-分钟计" aria-label="Permalink to &quot;**工作时段声音提示 (以 `25` 分钟计)**&quot;">​</a></h3><p>工作时钟会按照预设的节奏提供声音提示，以引导工作流程：</p><ul><li><strong>开始</strong>：提示音响起，工作周期启动。</li><li><strong>Review (r)</strong>：<code>2</code> 分钟回顾阶段提示。</li><li><strong>Work (w)</strong>：第一个 <code>10.5</code> 分钟工作阶段提示。</li><li><strong>Work (w)</strong>：第二个 <code>10.5</code> 分钟工作阶段提示。</li><li><strong>Review (r)</strong>：<code>1</code> 分钟回顾阶段提示。</li><li><strong>Track (t)</strong>：<code>1</code> 分钟记录阶段（无声音提示）。</li><li><strong>结束</strong>：提示音响起，工作周期完成。</li></ul><h3 id="休息时段声音提示" tabindex="-1"><strong>休息时段声音提示</strong> <a class="header-anchor" href="#休息时段声音提示" aria-label="Permalink to &quot;**休息时段声音提示**&quot;">​</a></h3><p>休息计时器会在关键节点提供声音提示：</p><ul><li><strong>开始</strong></li><li><strong>1/5</strong></li><li><strong>2/5</strong></li><li><strong>3/5</strong></li><li><strong>结束</strong></li></ul><h2 id="_2-连续计时模式-pizza-mode-与设置-setting" tabindex="-1">2 连续计时模式 (Pizza Mode)与设置（Setting） <a class="header-anchor" href="#_2-连续计时模式-pizza-mode-与设置-setting" aria-label="Permalink to &quot;2 连续计时模式 (Pizza Mode)与设置（Setting）&quot;">​</a></h2><p>此模式专为需要连续执行多个工作和休息周期的用户设计，以及提供设置选项。 <img src="'+a+'" alt="Pizza Mode" width="200"></p><ul><li><strong>模式切换</strong>：点击 🍅🍕 按钮，可在单次计时与连续计时模式之间进行切换，时钟正在运行时无法切换。</li><li><strong>自定义序列</strong>：在文本框中输入指令，以设定连续运行的任务序列。 <ul><li><strong>示例 1</strong>：输入 <code>🍅+05</code>，代表执行一个 <code>25min</code> 的工作周期，然后自动开始一个 <code>5min</code> 的休息。</li><li><strong>示例 2</strong>：输入 <code>05+05</code>，代表连续执行两个 <code>5min</code> 的休息周期。</li></ul></li><li><strong>调整默认工作时长</strong>：通过 <code>🍅=25min</code> 的设置项，您可以修改默认工作时钟的长度，支持 <code>15min</code> 至 <code>59min</code> 的自定义。</li><li><strong>白噪音</strong>：点击喇叭 🔈 按钮，可以开启或关闭背景白噪音，默认关闭。</li><li><strong>白噪音种类</strong>：点击红点，选择滴答声或雨声。</li></ul><h2 id="_3-迷你计时器模式-on-top-mode" tabindex="-1">3 迷你计时器模式 (On top Mode) <a class="header-anchor" href="#_3-迷你计时器模式-on-top-mode" aria-label="Permalink to &quot;3 迷你计时器模式 (On top Mode)&quot;">​</a></h2><p>为追求极致专注的用户提供无干扰的计时体验。</p><ol><li><strong>启动模式</strong>：点击<code>菜单栏</code> <img src="'+n+'" width="20" style="display:inline-block;vertical-align:middle;margin:0;background:rgb(224, 224, 224);border-radius:6px;"> 按钮，将隐藏界面上除计时器外的所有元素。</li><li><strong>退出模式</strong>：点击计时器界面 <img src="'+d+'" width="20" style="display:inline-block;vertical-align:middle;margin:0;">，退出迷你模式。</li><li><strong>多实例运行</strong>：可以同时打开多个计时器应用，并将其中一个或多个设置为迷你模式。</li><li><strong>跨设备使用 (macOS)</strong>：利用 macOS 的 Sidecar 功能，您可以将迷你版计时器窗口拓展至 iPad 等其他设备上显示。</li></ol><hr><h2 id="_4-重要说明" tabindex="-1">4 重要说明 <a class="header-anchor" href="#_4-重要说明" aria-label="Permalink to &quot;4 重要说明&quot;">​</a></h2><ul><li>在计时器运行时，无法切换 <code>单次/连续</code> 模式，因此单次模式运行中无法关闭白噪音。</li><li>在某些平台上，窗口可能会出现尺寸计算错误。若发生此情况，请尝试重复开启迷你模式，程序将自动重新计算并修正窗口尺寸。</li></ul>',20)]))}const v=e(s,[["render",c]]);export{w as __pageData,v as default};
